    private void btnClientSendActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnClientSendActionPerformed
        send();
    }//GEN-LAST:event_btnClientSendActionPerformed

    private void send() {
        String msg = txfClientMsg.getText().trim();
        txfClientMsg.setText("");
        client.sendMessage(new ChatMessage(ChatMessage.MESSAGE, msg));
    }

    private void btnJoinActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnJoinActionPerformed
        DefaultCaret caret = (DefaultCaret)txaClientOut.getCaret();
        caret.setUpdatePolicy(DefaultCaret.OUT_BOTTOM);
        String username = txfEnterUsername.getText().trim();
        String serverAddress = txfServer.getText().trim();
        if (username.equals("")) {
            txaClientOut.setText(" *** Please enter a username. *** ");
        } else if (serverAddress.equals("")) {
            txaClientOut.append(" *** Please enter a server address. *** ");
        } else {
            int portNumber = (int) spnPort.getValue();
            client = new Client(serverAddress, portNumber, username);
            if (!client.start()) {
                txaClientOut.setText(" *** Error starting client *** \n");
            } else {
                String initialMessage = client.readMessage();
                if (initialMessage.equals("true")) {
                    client.startListeningFromServer();
                    txaClientOut.setText("Welcome to the chatroom.\n");
                    txaClientOut.append("To broadcast a message to all connected users just type a message\nand press send.\n");
                    txaClientOut.append("To send a private message, type @<username> <message>.\n\n");
                    btnClientSend.setEnabled(true);
                    txfEnterUsername.setEditable(false);
                    btnJoin.setEnabled(false);
                    btnLogout.setEnabled(true);
                    btnCheckConnected.setEnabled(true);
                    spnPort.setEnabled(false);
                } else if (initialMessage.equals("false")) {
                    txaClientOut.setText("This username already exists. Try using a new one.");
                }
            }
        }
    }//GEN-LAST:event_btnJoinActionPerformed


    private void btnLogoutActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLogoutActionPerformed
        close();
    }//GEN-LAST:event_btnLogoutActionPerformed

    private void close() {
        isRunning = false;
        client.disconnect();
        setVisible(false);
        dispose();
        System.exit(0);
    }

    private void formWindowClosing(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowClosing
        close();
    }//GEN-LAST:event_formWindowClosing

    private void btnCheckConnectedActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCheckConnectedActionPerformed
        String msg = txfClientMsg.getText().trim();
        txfClientMsg.setText("");
        client.sendMessage(new ChatMessage(ChatMessage.WHOISIN, ""));
    }//GEN-LAST:event_btnCheckConnectedActionPerformed

