    private void txfClientMsgKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txfClientMsgKeyPressed
        if (evt.getKeyChar() == KeyEvent.VK_ENTER) {
            send();
        }
    }//GEN-LAST:event_txfClientMsgKeyPressed

